        var howManyFlames;
        var x;
        var y;

        // Calculate minY
        minY = 14 - this.timerTime;
        if (minY < 5)
            minY = 5;

        // Caulcate howManyFlames
        howManyFlames = this.timerTime * 25;
        if (howManyFlames > 200)
            howManyFlames = 200;

        // Reset the array
        this.resetFlamesArray();

